import styles from './Card.module.css'
import { Link } from 'react-router-dom'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCircleXmark, faHeartCircleMinus, faHeartCirclePlus } from '@fortawesome/free-solid-svg-icons'
import { addFav, removeFav } from '../../redux/actions/actions'
import { useState, useEffect } from 'react'
import { connect } from 'react-redux'

const Card = ({ id, name, image, onClose, addFav, removeFav, myFavorites }) => {
	const [isFav, setIsFav] = useState(false)

	useEffect(() => {
		myFavorites.forEach((fav) => {
			if (fav.id === this.props.id) {
				setIsFav(true)
			}
		})
	}, [myFavorites])

	// const nameFit = name.length > 12 ? `${name.substring(0, 12)}...` : name

	const handleFavorite = () => {
		if (isFav) {
			removeFav(id)
			setIsFav(false)
		} else {
			addFav(this.props)
			setIsFav(false)
		}
	}

	return (
		<div className={styles.Card}>
			<div className={styles.Header}>
				<h1 className={styles.Title}>
					{name}
					{isFav ? (
						<button className={styles.removeFav} onClick={handleFavorite}>
							<FontAwesomeIcon icon={faHeartCircleMinus} />
						</button>
					) : (
						<button className={styles.addFav} onClick={handleFavorite}>
							<FontAwesomeIcon icon={faHeartCirclePlus} />
						</button>
					)}
				</h1>
				<div className={styles.ButtonContainer}>
					<button className={styles.CloseBtn} onClick={() => onClose(id)}>
						<FontAwesomeIcon icon={faCircleXmark} />
					</button>
				</div>
			</div>
			<Link to={`/detail/${id}`} className={styles.Inner}>
				<h3> Click para m√°s info </h3>
				<img className={styles.CardImg} src={image} alt="" />
			</Link>
		</div>
	)
}
export const mapDispatchToProps = (dispatch) => {
	return {
		addFav: (char) => {
			dispatch(addFav(char))
		},
		removeFav: (id) => {
			dispatch(removeFav(id))
		},
	}
}

export const mapStateToProps = (state) => {
	return {
		myFavorites: state.myFavorites,
	}
}

// export default Card
export default connect(mapStateToProps, mapDispatchToProps)(Card)
