import styles from './Form.module.css'
import { useEffect, useState } from 'react'
import axios from 'axios'

const Form = () => {
	const [img, setImg] = useState('')

	const [userData, setUserData] = useState({
		email: '',
		password: '',
	})

	const [errors, setErrors] = useState({})

	useEffect(() => {
		const value = Math.floor(Math.random() * 827)
		axios(`https://rickandmortyapi.com/api/character/${value}`).then(({ data }) => {
			if (data.name) {
				setImg(data.image)
			}
		})
		return img
	}, [])
	return (
		<div className={styles.container}>
			<img src={img} alt="Character" />
			<div className={styles.portal}></div>
			<form className={styles.data}>
				<label>
					<h2>E-mail</h2>
				</label>
				<input value={userData.email} name="email" className={styles.input}></input>
				<label>
					<h2>Password</h2>
				</label>
				<input value={userData.password} name="password" className={styles.input}></input>
				<button className={styles.button}>Submit</button>
			</form>
		</div>
	)
}

export default Form
