import styles from './Card.module.css'
import { Link } from 'react-router-dom'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCircleXmark } from '@fortawesome/free-solid-svg-icons'
import { connect } from 'react-redux'

const Card = ({ id, name, image, onClose }) => {
	const nameFit = (name) => {
		const maxChar = 12
		if (name.length > maxChar) {
			return `${name.substring(0, maxChar)}...`
		} else {
			return name
		}
	}
	return (
		<div className={styles.Card}>
			<div className={styles.front}>
				<div className={styles.Header}>
					<h1 className={styles.Title}>{nameFit}</h1>
					<div className={styles.ButtonContainer}>
						<button className={styles.CloseBtn} onClick={() => onClose(id)}>
							<FontAwesomeIcon icon={faCircleXmark} />
						</button>
					</div>
				</div>
				<Link to={`/detail/${id}`} className={styles.Inner}>
					<h3> Click para m√°s info </h3>
					<img className={styles.CardImg} src={image} alt="" />
				</Link>
			</div>
		</div>
	)
}

export default Card
