import styles from './Card.module.css'
import { Link } from 'react-router-dom'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCircleXmark } from '@fortawesome/free-solid-svg-icons'
import { addFav, removeFav } from '../../redux/actions/actions'
import { useState } from 'react'
import { connect } from 'react-redux'

const Card = ({ id, name, image, onClose, addFav, removeFav }) => {
	const [isFav, setIsFav] = useState(false)

	const nameFit = name.length > 15 ? `${name.substring(0, 15)}...` : name

	const handleFavorite = () => {
		if (isFav) {
			removeFav(id)
			setIsFav(false)
		} else {
			addFav(this.props)
			setIsFav(false)
		}
	}

	return (
		<div className={styles.Card}>
			<div className={styles.front}>
				<div className={styles.Header}>
					<h1 className={styles.Title}>{nameFit}</h1>
					<div className={styles.ButtonContainer}>
						<button className={styles.CloseBtn} onClick={() => onClose(id)}>
							<FontAwesomeIcon icon={faCircleXmark} />
						</button>
					</div>
				</div>
				<Link to={`/detail/${id}`} className={styles.Inner}>
					<h3> Click para m√°s info </h3>
					<img className={styles.CardImg} src={image} alt="" />
				</Link>
			</div>
		</div>
	)
}
export const mapDispatchToProps = (dispatch) => {
	return {
		addFav: (char) => {
			dispatch(addFav(char))
		},
		removeFav: (id) => {
			dispatch(removeFav(id))
		},
	}
}

export default connect(null, mapDispatchToProps)(Card)
